// Example: Add a new user via IMS API
POST /api/users
{
  "username": "jdoe",
  "role": "Manager",
  "email": "jdoe@example.com"
}

function checkPermission(userRole, permission) {
  const roles = {
    admin: ['read', 'write', 'delete'],
    manager: ['read', 'write'],
    viewer: ['read']
  };
  return roles[userRole].includes(permission);
}
